import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { BsHeartFill } from "react-icons/bs";
import axios from "axios";
import "./frontend.css";

const MusicStore = () => {
  const [stores, setStores] = useState([]);
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/music-stores`
        );
        const fetchedStores = response.data;

        const storedFavorites = JSON.parse(localStorage.getItem("favorites")) || [];

        const storesWithFavorites = fetchedStores.map((store) => ({
          ...store,
          isFavorite: storedFavorites.some((fav) => fav._id === store._id),
        }));

        setStores(storesWithFavorites);
        setFavorites(storedFavorites);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const toggleFavorite = (store) => {
    const savedFavorites = JSON.parse(localStorage.getItem("favorites")) || [];
    const isAlreadyFavorite = savedFavorites.some((fav) => fav._id === store._id);

    const updatedFavorites = isAlreadyFavorite
      ? savedFavorites.filter((fav) => fav._id !== store._id)
      : [...savedFavorites, store];

    setFavorites(updatedFavorites);
    localStorage.setItem("favorites", JSON.stringify(updatedFavorites));

    const updatedStores = stores.map((s) =>
      s._id === store._id ? { ...s, isFavorite: !isAlreadyFavorite } : s
    );
    setStores(updatedStores);
  };

  const isFavorite = (store) => {
    return favorites.some((fav) => fav._id === store._id);
  };

  return (
    <div className="mainFront">
      <div className="container-fluid" id="explore">
        <section className="artSection">
          <h2 className="my-2 artCat">Music Stores</h2>
          <div
            className="storeGrid" // Grid layout
            style={{
              display: "grid",
              gridTemplateColumns: "repeat(6, 1fr)", // 6 items per row
              gap: "20px",
            }}
          >
            {stores.map((store) => (
              <div
                key={store._id}
                className="storeCard"
                style={{
                  textAlign: "center",
                  padding: "10px",
                }}
              >
                <Link to={`/music-store/${store._id}`}>
                  <div className="storeImage">
                    {store.logo && (
                      <img
                        src={`${process.env.REACT_APP_API_URL}/${store.logo}`}
                        alt={store.name}
                        width="100%"
                        loading="lazy"
                      />
                    )}
                    <div className="storeContent">
                      <h4 className="storeTitle">{store.name}</h4>
                    </div>
                  </div>
                </Link>

                {/* Add heart icon for favorites */}
                <div className="favoriteIcon">
                  <button onClick={() => toggleFavorite(store)}>
                    {isFavorite(store) ? (
                      <BsHeartFill className="favorited" />
                    ) : (
                      <BsHeartFill className="heartIcon" />
                    )}
                  </button>
                </div>
              </div>
            ))}
          </div>
        </section>
      </div>
    </div>
  );
};

export default MusicStore;
