import React, { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import { BsHeartFill } from "react-icons/bs";
import { BsChevronCompactRight, BsChevronCompactLeft } from "react-icons/bs";
import { gsap } from "gsap";
import { Draggable } from "gsap/Draggable";
import axios from "axios";
import "./frontend.css";

gsap.registerPlugin(Draggable);

const WeddingVIP = () => {
  const [weddingVIPItems, setWeddingVIPItems] = useState([]); // Changed the name from weddingVIPArtists to weddingVIPItems
  const [favorites, setFavorites] = useState([]);
  const [showArrows, setShowArrows] = useState({ left: false, right: false });
  const carouselRefs = useRef([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/weddingvip`
        );
        const fetchedItems = response.data;

        // Filter to only get the "Wedding VIP" category
        const weddingVIPCategoryItems = fetchedItems.filter(
          (item) => item.category === "Wedding VIP" // Updated to item
        );

        // Load favorites from localStorage
        const storedFavorites =
          JSON.parse(localStorage.getItem("favorites")) || [];

        // Ensure favorites are marked correctly
        const updatedItems = weddingVIPCategoryItems.map((item) => ({
          ...item,
          isFavorite: storedFavorites.some((fav) => fav._id === item._id),
        }));

        // Update state with Wedding VIP items
        setWeddingVIPItems(updatedItems); // Updated to setWeddingVIPItems
        setFavorites(storedFavorites);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  // The rest of your useEffect hooks remain unchanged

  const toggleFavorite = (item) => { // Updated to item
    const savedFavorites = JSON.parse(localStorage.getItem("favorites")) || [];
    const isAlreadyFavorite = savedFavorites.some(
      (fav) => fav._id === item._id // Updated to item
    );

    const updatedFavorites = isAlreadyFavorite
      ? savedFavorites.filter((fav) => fav._id !== item._id) // Updated to item
      : [...savedFavorites, item]; // Updated to item

    setFavorites(updatedFavorites);
    localStorage.setItem("favorites", JSON.stringify(updatedFavorites));
  };

  const isFavorite = (item) => { // Updated to item
    return favorites.some((fav) => fav._id === item._id); // Updated to item
  };

  const scrollCarousel = (direction, index) => {
    const carousel = carouselRefs.current[index];
    if (carousel) {
      const slideWidth = carousel.clientWidth / 6; // Adjust the denominator based on the number of visible slides
      const scrollAmount = slideWidth * 3 * direction;

      carousel.scrollBy({
        left: scrollAmount,
        behavior: "smooth",
      });

      setTimeout(() => {
        const scrollWidth = carousel.scrollWidth;
        const clientWidth = carousel.clientWidth;
        const scrollLeft = carousel.scrollLeft;

        setShowArrows((prev) => ({
          ...prev,
          [index]: {
            left: scrollLeft > 0,
            right: scrollLeft < scrollWidth - clientWidth,
          },
        }));
      }, 500);
    }
  };

  return (
    <div className="mainFront">
      <div className="container-fluid p-0" id="explore">
        <section className="artSection">
          <div className="div mb-2 ">
            <h2 className="artCat">Wedding VIP</h2>
            <hr />
          </div>
          {showArrows[0]?.left && (
            <button
              className="arrow left react-multiple-carousel__arrow"
              onClick={() => scrollCarousel(-1, 0)} // Scroll left
            >
              <BsChevronCompactLeft />
            </button>
          )}
          <div
            className="artistCarousel px-3 mb-2"
            ref={(el) => (carouselRefs.current[0] = el)}
            style={{
              display: "flex",
              overflow: "hidden",
              width: "100%",
            }}
          >
            {weddingVIPItems?.map((item) => ( // Updated to item
              <div
                key={item._id} // Updated to item
                className="artistImage"
                style={{
                  flex: "0 0 16.67%",
                  boxSizing: "border-box",
                  padding: "0 5px",
                }}
              >
                <Link to={`/weddingvip/${item._id}`}> {/* Updated path */}
                  <div className="artistImage">
                    {item.imageUrl && ( // Updated to item
                      <img
                        src={`${process.env.REACT_APP_API_URL}/${item.imageUrl}`} // Updated to item
                        alt={item.title} // Updated to item
                        width="100%"
                        loading="lazy"
                      />
                    )}
                    <div className="artContent">
                      <h4 className="artTitle">{item.title}</h4> {/* Updated to item */}
                    </div>
                  </div>
                </Link>

                <div className="favoriteIcon">
                  <button onClick={() => toggleFavorite(item)}> {/* Updated to item */}
                    {isFavorite(item) ? ( // Updated to item
                      <BsHeartFill className="favorited" />
                    ) : (
                      <BsHeartFill className="heartIcon " />
                    )}
                  </button>
                </div>
              </div>
            ))}
          </div>
          {showArrows[0]?.right && (
            <button
              className="arrow right react-multiple-carousel__arrow"
              onClick={() => scrollCarousel(1, 0)} // Scroll right
            >
              <BsChevronCompactRight />
            </button>
          )}
        </section>
      </div>
    </div>
  );
};

export default WeddingVIP;
