import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./frontend.css";

const MusicStore = ({ onNavigate }) => {
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/musicstore`
        );
        const publishedStores = response.data.filter(
          (store) => store.status === "published"
        );
        setStores(publishedStores);
        setLoading(false);
      } catch (error) {
        setError("Failed to fetch stores. Please try again later.");
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const LazyStoreCard = ({ store }) => {
    const [isVisible, setIsVisible] = useState(false);
    const [imageLoaded, setImageLoaded] = useState(false); // Track if image is loaded
    const ref = useRef();

    useEffect(() => {
      const observer = new IntersectionObserver(
        ([entry]) => {
          if (entry.isIntersecting) {
            setIsVisible(true);
          }
        },
        { threshold: 0.2 }
      );
      if (ref.current) observer.observe(ref.current);
      return () => {
        if (ref.current) observer.unobserve(ref.current);
      };
    }, []);

    const handleClick = () => {
      if (onNavigate) {
        onNavigate(`/music-store/${store._id}`);
      }
    };

    return (
      <div
        ref={ref}
        key={store._id}
        className="storeCard"
        style={{ textAlign: "center", padding: "10px" }}
        onClick={handleClick}
      >
        {isVisible && (
          <div className="storeImage">
            {/* Placeholder before image is loaded */}
            {!imageLoaded && <div className="placeholder" />}
            <img
              src={
                store.featuredImage
                  ? `${process.env.REACT_APP_API_URL}/${store.featuredImage}`
                  : "/placeholder.png"
              }
              alt={store.name}
              loading="lazy"
              onLoad={() => setImageLoaded(true)} // Set image as loaded once it is available
            />
            <div className="storeContent">
              <h4 className="artTitle">{store.name}</h4>
            </div>
          </div>
        )}
      </div>
    );
  };

  if (loading) return <div className="loading-spinner">Loading...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="bg-custom">
      <h2 className="my-2 fav-title">Music Stores</h2>
      <div className="container-fluid">
        <div className="storeGrid">
          {stores.map((store) => (
            <LazyStoreCard key={store._id} store={store} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default MusicStore;
