import React, { useState } from 'react';
import axios from 'axios';

const AddMusicStore = () => {
  const [musicStore, setMusicStore] = useState({
    storeName: '',
    bio: '',
    location: '',
    contact: '',
    logo: null,
    featuredImage: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setMusicStore((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setMusicStore((prev) => ({
      ...prev,
      [name]: files[0], // Store the first file selected
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    // Append form data
    formData.append('storeName', musicStore.storeName);
    formData.append('bio', musicStore.bio);
    formData.append('location', musicStore.location);
    formData.append('contact', musicStore.contact);
    
    if (musicStore.logo) {
      formData.append('logo', musicStore.logo);
    }
    
    if (musicStore.featuredImage) {
      formData.append('featuredImage', musicStore.featuredImage);
    }

    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/musicStores`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log(response.data);
      // Handle success (e.g., redirect or show a message)
    } catch (error) {
      console.error(error);
      // Handle error (e.g., show an error message)
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="storeName"
        value={musicStore.storeName}
        onChange={handleChange}
        placeholder="Store Name"
        required
      />
      <textarea
        name="bio"
        value={musicStore.bio}
        onChange={handleChange}
        placeholder="Bio"
        required
      />
      <input
        type="text"
        name="location"
        value={musicStore.location}
        onChange={handleChange}
        placeholder="Location"
        required
      />
      <input
        type="text"
        name="contact"
        value={musicStore.contact}
        onChange={handleChange}
        placeholder="Contact"
        required
      />
      <input
        type="file"
        name="logo"
        onChange={handleFileChange}
        className="form-control"
      />
      <input
        type="file"
        name="featuredImage" // Corrected field name
        onChange={handleFileChange}
        className="form-control"
      />
      <button type="submit">Add Music Store</button>
    </form>
  );
};

export default AddMusicStore;
