import React, { useRef, useState, useEffect } from 'react';
import { gsap } from 'gsap';

const Banner = () => {
  const slides = [
    {
      src: '/banner1.webp',
      alt: 'Banner for Musicians',
      text: 'Explore the World of Musicians',
      button: 'Explore Now',
    },
    {
      src: '/banner2.webp',
      alt: 'Banner for Venues',
      text: 'Find the Perfect Venue',
      button: 'Explore Venues',
    },
    {
      src: '/banner3.webp',
      alt: 'Banner for Music Stores',
      text: 'Shop at Top Music Stores',
      button: 'Checkout Stores',
    },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const carouselRef = useRef();

  // GSAP Animation: Function to animate slide changes
  const animateSlide = (index) => {
    const xOffset = `-${index * 100}%`; // Calculate horizontal offset
    gsap.to(carouselRef.current, {
      x: xOffset,
      duration: 2,
      ease: 'power2.inOut',
    });
  };

  // Handle Auto-play
  useEffect(() => {
    const interval = setInterval(() => {
      const nextIndex = (currentIndex + 1) % slides.length;
      setCurrentIndex(nextIndex);
      animateSlide(nextIndex);
    }, 8000); // Change slides every 3 seconds

    return () => clearInterval(interval); // Clear interval on component unmount
  }, [currentIndex]);

  // Handle Dot Clicks
  const handleDotClick = (index) => {
    setCurrentIndex(index);
    animateSlide(index);
  };

  return (
    <div className="carousel-container" style={styles.carouselContainer}>
      {/* Carousel Track */}
      <div
        className="carousel-track"
        style={styles.carouselTrack}
        ref={carouselRef}
      >
        {slides.map((slide, index) => (
          <div key={index} className="carousel-slide" style={styles.slide}>
            <img src={slide.src} alt={slide.alt} style={styles.image} />
            <h1 className="carousel-text" style={styles.text}>
              {slide.text}
            </h1>

            <button className="btn enquirybtn" style={styles.button}>
              {slide.button}
            </button>

          </div>
        ))}
      </div>

      {/* Dot Navigation */}
      <div className="carousel-dots" style={styles.dots}>
        {slides.map((_, index) => (
          <span
            key={index}
            onClick={() => handleDotClick(index)}
            style={{
              ...styles.dot,
              backgroundColor: currentIndex === index ? '#000' : '#ccc',
            }}
          ></span>
        ))}
      </div>
    </div>
  );
};

// Styles
const styles = {
  carouselContainer: {
    position: 'relative',
    width: '100%',
    maxWidth: '100%',
    
    margin: 'auto',
    overflow: 'hidden',
  },
  carouselTrack: {
    display: 'flex',
    willChange: 'transform',
  },
  slide: {
    flex: '0 0 100%',
    textAlign: 'center',
    position: 'relative',
  },
  image: {
    width: '100%',
    height: '550px',
    objectFit: 'cover',
  },
  text: {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: '100%',
    color: 'white',
    padding: '15px 25px',
    borderRadius: '5px',
    textAlign: 'center',
    fontSize: '60px',
    fontWeight: 'bold',
  },
  button: {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',

  },

  dots: {
    display: 'flex',
    justifyContent: 'center',
    marginTop: '10px',
  },
  dot: {
    width: '12px',
    height: '12px',
    borderRadius: '50%',
    margin: '0 5px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
};

export default Banner;
