import React, { useState } from 'react';

const Banner = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const slides = [
    {
      src: '/banner1.webp',
      alt: 'Banner for Musicians',
      text: 'Explore the World of Musicians',
    },
    {
      src: '/banner2.webp',
      alt: 'Banner for Venues',
      text: 'Find the Perfect Venue',
    },
    {
      src: '/banner3.webp',
      alt: 'Banner for Music Stores',
      text: 'Shop at Top Music Stores',
    },
  ];

  const handlePrev = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? slides.length - 1 : prevIndex - 1
    );
  };

  const handleNext = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === slides.length - 1 ? 0 : prevIndex + 1
    );
  };

  return (
    <div className="carousel-container" style={styles.carouselContainer}>
      <div className="carousel-slide" style={styles.slide}>
        <img
          src={slides[currentIndex].src}
          alt={slides[currentIndex].alt}
          style={styles.image}
        />
        <div className="carousel-text" style={styles.text}>
          {slides[currentIndex].text}
        </div>
      </div>
      <button onClick={handlePrev} style={styles.button}>
        ❮
      </button>
      <button onClick={handleNext} style={styles.button}>
        ❯
      </button>
    </div>
  );
};

const styles = {
  carouselContainer: {
    position: 'relative',
    width: '100%',
    maxWidth: '800px',
    margin: 'auto',
    overflow: 'hidden',
  },
  slide: {
    position: 'relative',
    textAlign: 'center',
  },
  image: {
    width: '100%',
    height: 'auto',
  },
  text: {
    position: 'absolute',
    bottom: '20px',
    left: '50%',
    transform: 'translateX(-50%)',
    backgroundColor: 'rgba(0, 0, 0, 0.6)',
    color: 'white',
    padding: '10px 20px',
    borderRadius: '5px',
  },
  button: {
    position: 'absolute',
    top: '50%',
    transform: 'translateY(-50%)',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    color: 'white',
    border: 'none',
    borderRadius: '50%',
    width: '40px',
    height: '40px',
    cursor: 'pointer',
    fontSize: '18px',
    lineHeight: '40px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
};

export default Banner;
