import React, { useEffect, useState } from 'react';
import './Modal.css'; // Ensure to import your CSS for styling

const MyModal = () => {
  const [isModalVisible, setIsModalVisible] = useState(false);

  // Function to create a cookie
  const createCookie = (name, value, days) => {
    let expires = '';
    if (days) {
      const date = new Date();
      date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
      expires = "; expires=" + date.toGMTString();
    }
    document.cookie = name + "=" + value + expires + "; path=/";
  };

  // Function to get a cookie value
  const getCookie = (name) => {
    const value = `; ${document.cookie}`;
    const parts = value.split(`; ${name}=`);
    if (parts.length === 2) return parts.pop().split(';').shift();
    return '';
  };

  useEffect(() => {
    // Check if the cookie exists and show the modal after 3 seconds
    if (getCookie('loadPopup') === '') {
      const timer = setTimeout(() => {
        setIsModalVisible(true);
        createCookie('loadPopup', true, 30); // Set cookie for 30 days
      }, 3000);

      // Cleanup timer on unmount
      return () => clearTimeout(timer);
    }
  }, []);

  // Function to close the modal
  const handleClose = () => {
    setIsModalVisible(false);
  };

  return (
    <>
      {isModalVisible && (
        <div className="modal fade show" tabIndex="-1" role="dialog" aria-labelledby="myLargeModalLabel" id="myModal" style={{ display: 'block' }}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Welcome</h5>
                <button type="button" className="close" onClick={handleClose} aria-label="Close">
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="modal-body">
                <p>Connecting you to the best live music, seven days a week</p>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={handleClose}>Close</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default MyModal;
