import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import "./frontend.css"; // Import your CSS for styling

const StoreDetail = () => {
  const { id } = useParams();
  const [store, setStore] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStore = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/musicstore/${id}`
        );
        setStore(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching store:", error);
        setError('Failed to fetch store. Please try again later.');
        setLoading(false);
      }
    };

    fetchStore();
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!store) return <div>No store found.</div>;

  return (
    <div className="store-detail bg-custom">
      <div className="container">

      {store.featuredImage && (
            <img
              src={`${process.env.REACT_APP_API_URL}/${store.featuredImage}`}
              alt={store.name}
              className="store-image mb-2"
              style={{ width: "100%", height: "auto" }}
            />
          )}

        <h1>{store.name}</h1>
        <div>
          {store.location && (
            <>
              <strong>Location:</strong> <span>{store.location}</span>
            </>
          )}
        </div>
        <div>
          {store.contact && (
            <>
              <strong>Contact:</strong> <span>{store.contact}</span>
            </>
          )}
        </div>

        <div id="description" className="mt-3">
          <div className="row">
            <div
              className={`col-md-${
                artist.galleryImages.length || artist.imageUrl ? "6" : "12"
              }`}
            >
              <h4>About</h4>
              <div
                dangerouslySetInnerHTML={{
                  __html:
                    artist.description ||
                    "<em>Description not available yet</em>",
                }}
              />
              {/* WhatsApp Share Button */}
              <div className="whatsapp-share mt-5">
                <a
                  href={whatsappShareUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="btn btn-success"
                >
                  <FaWhatsapp /> Share the Artist
                </a>
              </div>
            </div>

            {(artist.galleryImages.length || artist.imageUrl) && (
              <div className={`col-md-${artist.imageUrl ? "6" : "12"}`}>
                {artist.imageUrl && (
                  <img
                    src={`${process.env.REACT_APP_API_URL}/${artist.imageUrl}`}
                    alt={artist.title}
                    className="artist-image mb-2"
                    style={{ width: "100%", height: "auto" }}
                  />
                )}

                {artist.galleryImages.length > 0 && (
                  <div className="gallery-container">
                    <h4>Gallery</h4>
                    <Gallery>
                      <div className="grid-container">
                        {images.map((img, index) => (
                          <Item
                            key={index}
                            original={img.src}
                            thumbnail={img.src}
                            width={img.width}
                            height={img.height}
                          >
                            {({ ref, open }) => (
                              <img
                                ref={ref}
                                onClick={open}
                                src={img.src}
                                alt={`Galleryimage ${index + 1}`}
                                className="grid-item"
                                style={{ cursor: "pointer" }}
                              />
                            )}
                          </Item>
                        ))}
                      </div>
                    </Gallery>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        
        <div>
          {store.bio && (
            <>
              <strong>Bio:</strong> <div dangerouslySetInnerHTML={{ __html: store.bio }} />
            </>
          )}
        </div>
        <div className="mt-3">
          
          {store.logo && (
            <img
              src={`${process.env.REACT_APP_API_URL}/${store.logo}`}
              alt={`${store.name} Logo`}
              className="store-logo"
              style={{ width: "100px", height: "auto" }}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default StoreDetail;
