import React, { useState, useEffect } from "react";
import axios from "axios";
import { CKEditor } from "@ckeditor/ckeditor5-react";
import ClassicEditor from "@ckeditor/ckeditor5-build-classic";
import { FaTimes } from 'react-icons/fa'; // Import Font Awesome icon (optional)

const EditMusicStore = ({ store, setEditStore, setShowAlert }) => {
  const [name, setName] = useState(store.name || "");
  const [location, setLocation] = useState(store.location || "");
  const [contact, setContact] = useState(store.contact || "");
  const [bio, setBio] = useState(store.bio || ""); // Added bio state
  const [logo, setLogo] = useState(store.logo || "");
  const [featuredImage, setFeaturedImage] = useState(null);
  const [newLogo, setNewLogo] = useState(null); // For new logo uploads
  const [errorMessage, setErrorMessage] = useState("");

  useEffect(() => {
    setName(store.name || "");
    setLocation(store.location || "");
    setContact(store.contact || "");
    setBio(store.bio || ""); // Initialize bio
    setLogo(store.logo || "");
    setFeaturedImage(store.featuredImage || ""); // Ensure featuredImage is set here
  }, [store]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (e.target.id === "featuredImage") {
      setFeaturedImage(file);
    } 
  };

  const handleRemoveLogo = async () => {
    try {
      await axios.delete(`${process.env.REACT_APP_API_URL}/api/musicstore/${store._id}/logo`);
      setLogo(null); // Clear the logo from state
      setShowAlert(true);
    } catch (error) {
      console.error("Error removing logo:", error);
      setErrorMessage("Error removing the logo. Please try again.");
    }
  };

  const handleRemoveFeaturedImage = async () => {
    try {
      await axios.delete(`${process.env.REACT_APP_API_URL}/api/musicstore/${store._id}/featured-image`); // Assuming you have this endpoint
      setFeaturedImage(null); // Clear the featured image from state
      setShowAlert(true);
    } catch (error) {
      console.error("Error removing featured image:", error);
      setErrorMessage("Error removing the featured image. Please try again.");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("name", name);
    formData.append("location", location);
    formData.append("contact", contact);
    formData.append("bio", bio); // Append bio
    if (newLogo) {
      formData.append("logo", newLogo); // Append the new logo if uploaded
    }
    if (featuredImage) {
      formData.append("musicStoreFeaturedImage", featuredImage); // Append the new featured image if uploaded
    }

    try {
      await axios.put(
        `${process.env.REACT_APP_API_URL}/api/musicstore/${store._id}`,
        formData,
        {
          headers: { "Content-Type": "multipart/form-data" },
        }
      );

      setShowAlert(true); // Show success alert
      setEditStore(null); // Close the edit form
    } catch (error) {
      console.error("Error updating music store:", error);
      setErrorMessage("Error updating the store. Please try again.");
    }
  };

  return (
    <div className="edit-music-store">
      <h3>Edit Music Store</h3>
      {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Store Name</label>
          <input
            type="text"
            className="form-control"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Location</label>
          <input
            type="text"
            className="form-control"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label>Contact</label>
          <input
            type="text"
            className="form-control"
            value={contact}
            onChange={(e) => setContact(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Bio</label>
          <CKEditor
            editor={ClassicEditor}
            data={bio}
            onChange={(event, editor) => {
              const data = editor.getData();
              setBio(data);
            }}
            config={{
              toolbar: [
                "heading",
                "|",
                "bold",
                "italic",
                "link",
                "|",
                "bulletedList",
                "numberedList",
                "blockQuote",
                "|",
                "undo",
                "redo",
              ],
              placeholder: "Enter the bio for the store here...",
            }}
          />
        </div>

        <div className="form-group">
          <label>Current Logo</label>
          {logo ? (
            <div>
              <img
                src={`${process.env.REACT_APP_API_URL}/${logo}`}
                alt={name}
                style={{ width: "100px" }}
              />
              <button type="button" onClick={handleRemoveLogo} style={{ marginLeft: '10px', border: 'none', background: 'none', cursor: 'pointer' }}>
                <FaTimes color="red" size={20} /> {/* Cross icon to remove logo */}
              </button>
            </div>
          ) : (
            <p>No Logo</p>
          )}
        </div>

        <div className="form-group">
          <label>Upload New Logo (optional)</label>
          <input
            type="file"
            className="form-control"
            onChange={handleFileChange}
          />
        </div>

        <div className="form-group">
          <label>Current Featured Image</label>
          {store.featuredImage ? (
            <div>
              <img
                src={`${process.env.REACT_APP_API_URL}/${store.featuredImage}`}
                alt={name}
                style={{ width: "100px" }}
              />
              <button type="button" onClick={handleRemoveFeaturedImage} style={{ marginLeft: '10px', border: 'none', background: 'none', cursor: 'pointer' }}>
                <FaTimes color="red" size={20} /> {/* Cross icon to remove featured image */}
              </button>
            </div>
          ) : (
            <p>No Featured Image</p>
          )}
        </div>

        <div className="form-group">
          <label>Featured Image</label>
          <input
            type="file"
            name="featuredImage"
            onChange={handleFileChange}
            className="form-control"
          />
        </div>

        <div className="form-group d-flex">
          <button type="submit" className="btn btn-primary">
            Save Changes
          </button>
          <button
            type="button"
            className="btn btn-secondary ml-2"
            onClick={() => setEditStore(null)} // Close the edit form
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditMusicStore;
