import React, { useEffect } from "react";

const WelcomeModal = () => {
  useEffect(() => {
    const modalShown = localStorage.getItem("welcomeModalShown");

    if (!modalShown) {
      setTimeout(() => {
        const modalElement = document.getElementById("welcomeModal");
        const modal = new window.bootstrap.Modal(modalElement);
        
        // Remove inert when the modal is shown
        modalElement.removeAttribute("inert");
        modal.show();
        localStorage.setItem("welcomeModalShown", "true");

        // Add event listeners for modal hidden
        modalElement.addEventListener("hidden.bs.modal", () => {
          modalElement.setAttribute("inert", ""); // Set inert when hidden
          document.querySelector("body").focus(); // Set focus back to body or another element
        });
      }, 3000);
    }
  }, []);

  return (
    <div className="modal fade" id="welcomeModal" tabIndex="-1" aria-labelledby="welcomeModalLabel" aria-hidden="true" inert={isModalVisible ? "true" : undefined}>

      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header p-0">
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
          </div>
          <div className="modal-body">
            <h4 className="modal-title text-center" id="welcomeModalLabel">
              Connecting you to the best live music, seven days a week
            </h4>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WelcomeModal;
