import React, { useState, useEffect } from 'react';
import Gallery from 'react-photo-gallery'; // Assuming you're using this package for gallery display
import { Item } from 'react-photoswipe-gallery'; // Assuming you're using this package for lightbox functionality
import axios from 'axios';

const VenueDetails = ({ match }) => {
  const [venue, setVenue] = useState({});
  const [formData, setFormData] = useState({ name: '', phone: '', email: '' });
  const [formMessage, setFormMessage] = useState('');

  useEffect(() => {
    const fetchVenue = async () => {
      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/venues/${match.params.id}`);
        setVenue(response.data);
      } catch (error) {
        console.error('Error fetching the venue data:', error);
      }
    };

    fetchVenue();
  }, [match.params.id]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Handle form submission logic
      setFormMessage('Your enquiry has been submitted successfully!');
    } catch (error) {
      console.error('Error submitting the form:', error);
      setFormMessage('There was an error submitting your enquiry. Please try again.');
    }
  };

  return (
    <div className="venue-detail bg-custom">
      <div className="container">
        <h1>{venue.title}</h1>

        <div id="description" className="mt-3">
          <div className="row">
            <div
              className={`col-md-${
                venue.gallery.length || venue.featuredImage ? '6' : '12'
              }`}
            >
              <h4>About</h4>
              <div
                dangerouslySetInnerHTML={{
                  __html:
                    venue.description ||
                    '<em>Description not available yet</em>',
                }}
              />
              <div>
                Location: <span>{venue.location}</span>
              </div>
              <div>
                Category: <span>{venue.category}</span>
              </div>
            </div>

            {(venue.gallery.length || venue.featuredImage) && (
              <div className={`col-md-${venue.featuredImage ? '6' : '12'}`}>
                {venue.featuredImage && (
                  <img
                    src={`${process.env.REACT_APP_API_URL}/${venue.featuredImage}`}
                    alt={venue.title}
                    className="venue-image mb-2"
                    style={{ width: '100%', height: 'auto' }}
                  />
                )}

                {venue.gallery.length > 0 && (
                  <div className="gallery-container">
                    <h4>Gallery</h4>
                    <Gallery>
                      <div className="grid-container">
                        {venue.gallery.map((img, index) => (
                          <Item
                            key={index}
                            original={img}
                            thumbnail={img}
                            width={800} // or dynamic dimensions if you have them
                            height={600} // or dynamic dimensions if you have them
                          >
                            {({ ref, open }) => (
                              <img
                                ref={ref}
                                onClick={open}
                                src={img}
                                alt={`Gallery image ${index + 1}`}
                                className="grid-item"
                                style={{ cursor: 'pointer' }}
                              />
                            )}
                          </Item>
                        ))}
                      </div>
                    </Gallery>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        <div className="venueForm mt-3">
          <h1 className="mx-2 my-2">Book Now</h1>
          <form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-md-6">
                <div className="form-group">
                  <input
                    type="text"
                    id="name"
                    name="name"
                    className="form-control"
                    value={formData.name}
                    onChange={handleChange}
                    required
                    placeholder="Your Name"
                  />
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-group">
                  <input
                    type="tel"
                    id="phone"
                    name="phone"
                    className="form-control"
                    value={formData.phone}
                    onChange={handleChange}
                    required
                    placeholder="Phone Number"
                  />
                </div>
              </div>
            </div>

            <div className="row">
              <div className="col-md-6">
                <div className="form-group">
                  <input
                    type="email"
                    id="email"
                    name="email"
                    className="form-control"
                    value={formData.email}
                    onChange={handleChange}
                    required
                    placeholder="Your Email"
                  />
                </div>
              </div>
              <div className="col-md-6">
                <button type="submit" className="btn enquirybtn">
                  Enquire Now
                </button>
              </div>
            </div>
          </form>
          {formMessage && <p className="form-message">{formMessage}</p>}
        </div>
      </div>
    </div>
  );
};

export default VenueDetails;
