import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import "./frontend.css"; // Import your CSS for styling

const StoreDetail = () => {
  const { id } = useParams();
  const [store, setStore] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStore = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/musicstore/${id}`
        );
        setStore(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching store:", error);
        setError("Failed to fetch store. Please try again later.");
        setLoading(false);
      }
    };

    fetchStore();
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!store) return <div>No store found.</div>;

  return (
    <div className="store-detail bg-custom">
      <div className="container">
        {store.featuredImage && (
          <img
            src={`${process.env.REACT_APP_API_URL}/${store.featuredImage}`}
            alt={store.name}
            className="store-image mb-2"
            style={{ width: "100%", height: "auto" }}
          />
        )}

        <h1>{store.name}</h1>
        <div>
          {store.location && (
            <>
              <strong>Location:</strong> <span>{store.location}</span>
            </>
          )}
        </div>
        <div>
          {store.contact && (
            <>
              <strong>Contact:</strong> <span>{store.contact}</span>
            </>
          )}
        </div>

        <div id="description" className="mt-3">
          <div className="row">
            <div className="col-md-6">
              <h4>Bio</h4>

              {store.bio && (
                <>
                  <div dangerouslySetInnerHTML={{ __html: store.bio }} />
                </>
              )}
            </div>

            <div className="col-md-6">
              {store.logo && (
                <img
                  src={`${process.env.REACT_APP_API_URL}/${store.logo}`}
                  alt={`${store.name} Logo`}
                  className="store-logo"
                  style={{ width: "100px", height: "auto" }}
                />
              )}
            </div>
          </div>
        </div>
        <div className="mt-3"></div>
      </div>
    </div>
  );
};

export default StoreDetail;
