import React, { useState } from 'react';
import axios from 'axios';
import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';

const AddMusicStore = () => {
  const [showAlert, setShowAlert] = useState(false);
  const [musicStore, setMusicStore] = useState({
    name: '',
    bio: '',
    location: '',
    contact: '',
    logo: null, // Updated to null for file upload
    featuredImage: null, // Added this for featured image
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setMusicStore((prevState) => ({ ...prevState, [name]: value }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    
    console.log('File selected:', file);
    
    if (e.target.name === 'musicStoreFeaturedImage') {
      setMusicStore((prevState) => ({ ...prevState, featuredImage: file })); // Save the file object
    } else {
      setMusicStore((prevState) => ({ ...prevState, [e.target.name]: file })); // Save the file object for logo
    }
  };
  

  const handleDescriptionChange = (event, editor) => {
    const data = editor.getData();
    setMusicStore((prevState) => ({ ...prevState, bio: data }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', musicStore.name);
    formData.append('bio', musicStore.bio);
    formData.append('location', musicStore.location);
    formData.append('contact', musicStore.contact);

    if (musicStore.logo) {
      formData.append('logo', musicStore.logo);
    }

    if (musicStore.featuredImage) {
      formData.append('musicStoreFeaturedImage', musicStore.featuredImage); // Append featuredImage
    }

    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/musicstore`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      console.log('Music store saved successfully:', response.data);
      console.log(musicStore.featuredImage);
      setShowAlert(true);

      setTimeout(() => {
        setShowAlert(false);
      }, 3000);
    } catch (error) {
      console.error('Error saving music store:', error);
    }
  };

  return (
    <div className="add-item">
      <h3>Add New Music Store</h3>
      <form onSubmit={handleSubmit} encType="multipart/form-data" className="mt-3">
        <div className="form-group">
          <label>Store Name</label>
          <input
            type="text"
            name="name"
            value={musicStore.name}
            onChange={handleChange}
            required
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label>Bio</label>
          <CKEditor
            editor={ClassicEditor}
            data={musicStore.bio}
            onChange={handleDescriptionChange}
            required
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label>Location</label>
          <input
            type="text"
            name="location"
            value={musicStore.location}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label>Contact</label>
          <input
            type="text"
            name="contact"
            value={musicStore.contact}
            onChange={handleChange}
            className="form-control"
          />
        </div>

        <div className="form-group">
          <label>Logo</label>
          <input
            type="file"
            name="logo" // File input for logo
            onChange={handleFileChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label>Featured Image</label>
          <input
            type="file"
            name="musicStoreFeaturedImage" // File input for featuredImage
            onChange={handleFileChange}
            className="form-control"
          />
        </div>

        {showAlert && (
          <div className="alert alert-success alert-dismissible fade show" role="alert">
            Store added successfully!
            <button type="button" className="close" onClick={() => setShowAlert(false)}>
              <span aria-hidden="true">&times;</span>
            </button>
          </div>
        )}

        <button type="submit" className="btn btn-lg btn-dark mt-5">Add Store</button>
      </form>
    </div>
  );
};

export default AddMusicStore;
