import React, { useState, useEffect } from "react";

const WelcomeModal = () => {
  const [isModalVisible, setIsModalVisible] = useState(false); // State to control modal visibility

  useEffect(() => {
    // Show the modal after 3 seconds
    const timer = setTimeout(() => {
      setIsModalVisible(true);
      const modal = new window.bootstrap.Modal(document.getElementById('welcomeModal'));
      modal.show();
    }, 3000);

    return () => clearTimeout(timer); // Cleanup timer on unmount
  }, []);

  // Handle modal close
  const handleClose = () => {
    setIsModalVisible(false);
  };

  return (
    <div className="modal fade" id="welcomeModal" tabIndex="-1" aria-labelledby="instantBookLabel" aria-hidden="true">
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header p-0">
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" onClick={handleClose}></button>
          </div>
          <div className="modal-body">
            <h4 className="modal-title text-center" id="instantBookLabel">Instant Book</h4>
            <p className="text-center" style={{ color: "#8e56e3" }}>(10AM to 6PM)</p>
            <h5 className="text-center mt-3">Instant Book, instant response. Weâ€™ll be in touch within the next few minutes.</h5>

            <form className="mt-4">
              <div className="row mb-3">
                <div className="col-md-6">
                  <label htmlFor="name" className="form-label">Name</label>
                  <input type="text" className="form-control" id="name" />
                </div>
                <div className="col-md-6">
                  <label htmlFor="phone" className="form-label">Phone Number</label>
                  <input type="tel" className="form-control" id="phone" />
                </div>
              </div>
              <div className="mb-3">
                <label htmlFor="eventVision" className="form-label">Share your event vision</label>
                <textarea className="form-control" id="eventVision" rows="3"></textarea>
              </div>
            </form>
          </div>
          <div className="modal-footer">
            <button type="button" className="btn instant-btn" data-bs-dismiss="modal" onClick={handleClose}>Confirm Booking</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WelcomeModal;
