import React, { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import { BsHeartFill } from "react-icons/bs";
import { BsChevronCompactRight, BsChevronCompactLeft } from "react-icons/bs";
import { gsap } from "gsap";
import { Draggable } from "gsap/Draggable";
import axios from "axios";
import "./frontend.css";

gsap.registerPlugin(Draggable);

const WeddingVIP = () => {
  const [weddingVIPArtists, setWeddingVIPArtists] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [showArrows, setShowArrows] = useState({ left: false, right: false });
  const carouselRefs = useRef([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const artistsResponse = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/weddingvip`
        );
        const fetchedArtists = artistsResponse.data;

        // Load favorites from localStorage
        const storedFavorites =
          JSON.parse(localStorage.getItem("favorites")) || [];

        // Ensure favorites are marked correctly
        const updatedArtists = fetchedArtists.map((artist) => ({
          ...artist,
          isFavorite: storedFavorites.some((fav) => fav._id === artist._id),
        }));

        // Update state with fetched artists
        setWeddingVIPArtists(updatedArtists);
        setFavorites(storedFavorites);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (carouselRefs.current.length > 0) {
      const updateArrowVisibility = () => {
        carouselRefs.current.forEach((carousel, index) => {
          if (carousel) {
            const scrollWidth = carousel.scrollWidth;
            const clientWidth = carousel.clientWidth;
            const scrollLeft = carousel.scrollLeft;

            setShowArrows((prev) => ({
              ...prev,
              [index]: {
                left: scrollLeft > 0,
                right: scrollLeft < scrollWidth - clientWidth,
              },
            }));
          }
        });
      };

      updateArrowVisibility(); // Initial check

      window.addEventListener("resize", updateArrowVisibility);
      return () =>
        window.removeEventListener("resize", updateArrowVisibility);
    }
  }, [weddingVIPArtists, favorites]);

  useEffect(() => {
    if (window.innerWidth <= 500 && carouselRefs.current.length > 0) {
      carouselRefs.current.forEach((carousel) => {
        if (carousel) {
          gsap.killTweensOf(carousel);

          Draggable.create(carousel, {
            type: "x",
            bounds: {
              minX: -carousel.scrollWidth + carousel.clientWidth,
              maxX: 0,
            },
            inertia: true,
            throwProps: true,
            edgeResistance: 0.65,
            onThrowUpdate: () => {
              gsap.to(carousel, { x: carousel._gsap.x, ease: "power2.out" });
            },
            snap: {
              x: (value) => Math.round(value / 16.67) * 200, // Adjust based on item width
            },
          });
        }
      });
    }
  }, [weddingVIPArtists, favorites]);

  const toggleFavorite = (artist) => {
    const savedFavorites = JSON.parse(localStorage.getItem("favorites")) || [];
    const isAlreadyFavorite = savedFavorites.some(
      (fav) => fav._id === artist._id
    );

    const updatedFavorites = isAlreadyFavorite
      ? savedFavorites.filter((fav) => fav._id !== artist._id)
      : [...savedFavorites, artist];

    setFavorites(updatedFavorites);
    localStorage.setItem("favorites", JSON.stringify(updatedFavorites));
  };

  const isFavorite = (artist) => {
    return favorites.some((fav) => fav._id === artist._id);
  };

  const scrollCarousel = (direction, index) => {
    const carousel = carouselRefs.current[index];
    if (carousel) {
      const slideWidth = carousel.clientWidth / 6; // Adjust the denominator based on the number of visible slides
      const scrollAmount = slideWidth * 3 * direction;

      carousel.scrollBy({
        left: scrollAmount,
        behavior: "smooth",
      });

      setTimeout(() => {
        const scrollWidth = carousel.scrollWidth;
        const clientWidth = carousel.clientWidth;
        const scrollLeft = carousel.scrollLeft;

        setShowArrows((prev) => ({
          ...prev,
          [index]: {
            left: scrollLeft > 0,
            right: scrollLeft < scrollWidth - clientWidth,
          },
        }));
      }, 500);
    }
  };

  return (
    <div className="mainFront">
      <div className="container-fluid p-0" id="explore">
        <section className="artSection">
          <div className="div mb-2 ">
            <h2 className="artCat">Wedding VIP</h2>
            <hr></hr>
          </div>
          {showArrows[0]?.left && (
            <button
              className="arrow left react-multiple-carousel__arrow"
              onClick={() => scrollCarousel(-1, 0)} // Scroll left
            >
              <BsChevronCompactLeft />
            </button>
          )}
          <div
            className="artistCarousel px-3 mb-2"
            ref={(el) => (carouselRefs.current[0] = el)}
            style={{
              display: "flex",
              overflow: "hidden",
              width: "100%",
            }}
          >
            {weddingVIPArtists?.map((artist) => (
              <div
                key={artist._id}
                className="artistImage"
                style={{
                  flex: "0 0 16.67%",
                  boxSizing: "border-box",
                  padding: "0 5px",
                }}
              >
                <Link to={`/artist/${artist._id}`}>
                  <div className="artistImage">
                    {artist.imageUrl && (
                      <img
                        src={`${process.env.REACT_APP_API_URL}/${artist.imageUrl}`}
                        alt={artist.title}
                        width="100%"
                        loading="lazy"
                      />
                    )}
                    <div className="artContent">
                      <h4 className="artTitle">{artist.title}</h4>
                    </div>
                  </div>
                </Link>

                <div className="favoriteIcon">
                  <button onClick={() => toggleFavorite(artist)}>
                    {isFavorite(artist) ? (
                      <BsHeartFill className="favorited" />
                    ) : (
                      <BsHeartFill className="heartIcon " />
                    )}
                  </button>
                </div>
              </div>
            ))}
          </div>
          {showArrows[0]?.right && (
            <button
              className="arrow right react-multiple-carousel__arrow"
              onClick={() => scrollCarousel(1, 0)} // Scroll right
            >
              <BsChevronCompactRight />
            </button>
          )}
        </section>
      </div>
    </div>
  );
};

export default WeddingVIP;
