import React, { useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import { BsHeartFill } from "react-icons/bs";
import { gsap } from "gsap";
import { Draggable } from "gsap/Draggable";
import "./frontend.css";

gsap.registerPlugin(Draggable);

const GSAPCarousel = ({ items, toggleFavorite, type }) => {
  const carouselRef = useRef(null);

  useEffect(() => {
    const carouselElement = carouselRef.current;
    
    gsap.to(carouselElement, {
      xPercent: -100 * (items.length - 1),
      ease: "none",
      scrollTrigger: {
        trigger: carouselElement,
        scrub: 1,
        pin: true,
        end: () => "+=" + carouselElement.offsetWidth,
      },
    });

    Draggable.create(carouselElement, {
      type: "x",
      bounds: { minX: -(carouselElement.scrollWidth - carouselElement.clientWidth), maxX: 0 },
      inertia: true,
    });

    return () => {
      Draggable.get(carouselElement)?.kill();
    };
  }, [items]);

  return (
    <div className="gsap-carousel" ref={carouselRef}>
      {items.map((item) => (
        <div key={item._id} className="carousel-item">
          <span
            className={`favorite ${item.isFavorite ? "favorited" : ""}`}
            onClick={() => toggleFavorite(item._id)}
            style={{ color: item.isFavorite ? "red" : "grey" }}
          >
            <BsHeartFill />
          </span>
          <Link to={`/${type}/${item._id}`}>
            <div className="itemImage">
              {item.imageUrl && (
                <img
                  src={`${process.env.REACT_APP_API_URL}/${item.imageUrl}`}
                  alt={item.title}
                  width="100%"
                  loading="lazy"
                />
              )}
              <div className="itemContent">
                <h4 className="itemTitle">{item.title}</h4>
              </div>
            </div>
          </Link>
        </div>
      ))}
    </div>
  );
};

export default GSAPCarousel;
