import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import emailjs from "emailjs-com";
import { Gallery, Item } from "react-photoswipe-gallery";
import "photoswipe/dist/photoswipe.css";
import "./frontend.css"; // Import the CSS file for styling

const VenueDetail = () => {
  const { id } = useParams();
  const [venue, setVenue] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState({ name: "", phone: "", email: "" });
  const [formMessage, setFormMessage] = useState("");
  const [images, setImages] = useState([]);

  useEffect(() => {
    const fetchVenue = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/venues/${id}`
        );
        setVenue(response.data);
        setLoading(false);
        // Load images to get their dimensions
        const imagePromises = response.data.galleryImages.map(async (img) => {
          const src = `${process.env.REACT_APP_API_URL}/${img}`;
          return new Promise((resolve) => {
            const image = new Image();
            image.src = src;
            image.onload = () =>
              resolve({ src, width: image.width, height: image.height });
          });
        });

        const loadedImages = await Promise.all(imagePromises);
        setImages(loadedImages);
      
        
      } catch (error) {
        console.error('Error fetching venue:', error);
        setError('Failed to fetch venue. Please try again later.');
        setLoading(false);
      }
    };

    fetchVenue();
  }, [id]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const templateParams = {
      name: formData.name,
      phone: formData.phone,
      email: formData.email,
      venueName: venue?.title, // Ensure venue name is included
    };

    emailjs
      .send(
        "service_3hkljmf",
        "template_hg6yimn",
        templateParams,
        "q1l_DC7jwQvu80xJ5"
      )
      .then((response) => {
        setFormMessage("Booking request sent successfully!");
        setFormData({ name: "", phone: "", email: "" });
      })
      .catch((error) => {
        setFormMessage("Failed to send booking request. Please try again.");
        console.error("Error sending booking request:", error);
      });
  };

  if (loading) return <div>Loading...</div>;

  if (error) return <div>Error: {error}</div>;

  if (!venue) return <div>No venue found.</div>;

  const galleryImages = venue.gallery || [];

  return (
    <div className="venue-detail bg-custom">
      <div className="container">
        <h1>{venue.title}</h1>
        <div>
          Location: <span>{venue.location}</span>
        </div>
        <div>
          Category: <span>{venue.category}</span>
        </div>
        <div id="description" className="mt-3">
          <div className="row">
            <div
              className={`col-md-${galleryImages.length || venue.featuredImage ? "6" : "12"}`}
            >
              <h4>About</h4>
              <div
                dangerouslySetInnerHTML={{
                  __html: venue.description || "<em>Description not available yet</em>",
                }}
              />
            </div>
            {(galleryImages.length || venue.featuredImage) && (
              <div className={`col-md-${venue.featuredImage ? "6" : "12"}`}>
                {venue.featuredImage && (
                  <img
                    src={`${process.env.REACT_APP_API_URL}/${venue.featuredImage}`}
                    alt={venue.title}
                    className="venue-image mb-2"
                    style={{ width: "100%", height: "auto" }}
                  />
                )}
                {galleryImages.length > 0 && (
                  <div className="grid-container">
                    <h4>Gallery</h4>
                    <Gallery>
                      <div className="grid-container">
                        {galleryImages.map((img, index) => (
                          <Item
                            key={index}
                            original={`${process.env.REACT_APP_API_URL}/${img}`}
                            thumbnail={`${process.env.REACT_APP_API_URL}/${img}`}
                            width={img.width}
                            height={img.height}
                          >
                            {({ ref, open }) => (
                              <img
                                ref={ref}
                                onClick={open}
                                src={`${process.env.REACT_APP_API_URL}/${img}`}
                                alt={`galleryimg ${index + 1}`}
                                className="grid-item"
                                style={{ cursor: "pointer", width: "100%", height: "auto" }}
                              />
                            )}
                          </Item>
                        ))}
                      </div>
                    </Gallery>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
        <div className="venueForm mt-3">
          <h1 className="mx-2 my-2">Book Now</h1>
          <form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-md-6">
                <div className="form-group">
                  <input
                    type="text"
                    id="name"
                    name="name"
                    className="form-control"
                    value={formData.name}
                    onChange={handleChange}
                    required
                    placeholder="Your Name"
                  />
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-group">
                  <input
                    type="tel"
                    id="phone"
                    name="phone"
                    className="form-control"
                    value={formData.phone}
                    onChange={handleChange}
                    required
                    placeholder="Phone Number"
                  />
                </div>
              </div>
            </div>
            <div className="row">
              <div className="col-md-6">
                <div className="form-group">
                  <input
                    type="email"
                    id="email"
                    name="email"
                    className="form-control"
                    value={formData.email}
                    onChange={handleChange}
                    required
                    placeholder="Your Email"
                  />
                </div>
              </div>
              <div className="col-md-6">
                <button type="submit" className="btn enquirybtn">
                  Enquire Now
                </button>
              </div>
            </div>
          </form>
          {formMessage && <p className="form-message">{formMessage}</p>}
        </div>
      </div>
    </div>
  );
};

export default VenueDetail;
