import React, { useEffect } from "react";
// Import Bootstrap CSS and JavaScript
import 'bootstrap/dist/css/bootstrap.min.css';
import '../bootstrap/dist/js/bootstrap.bundle.min.js';


const WelcomeModal = () => {
  useEffect(() => {
    // Ensure bootstrap Modal is available and not undefined
    const bootstrap = window.bootstrap;
    if (!bootstrap) {
      console.error("Bootstrap is not loaded");
      return;
    }

    const modalShown = localStorage.getItem("welcomeModalShown");

    if (!modalShown) {
      // Show modal after 3 seconds
      setTimeout(() => {
        const modalElement = document.getElementById("welcomeModal");
        const welcomeModal = new bootstrap.Modal(modalElement); // Initialize the modal

        // Show the modal
        welcomeModal.show();

        // Mark modal as seen in localStorage
        localStorage.setItem("welcomeModalShown", "true");
      }, 3000);
    }
  }, []);

  return (
    <div className="modal fade" id="welcomeModal" tabIndex="-1" aria-labelledby="welcomeModalLabel" aria-hidden="true">
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header p-0">
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
          </div>
          <div className="modal-body">
            <h4 className="modal-title text-center" id="welcomeModalLabel">
              Connecting you to the best live music, seven days a week
            </h4>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WelcomeModal;
