import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js'; // Ensure Bootstrap JS is imported

const WelcomeModal = () => {
  const [isModalVisible, setIsModalVisible] = useState(false); // State to control modal visibility

  useEffect(() => {
    // Show the modal after 3 seconds
    const timer = setTimeout(() => {
      setIsModalVisible(true);
      const modalElement = document.getElementById('welcomeModal');

      if (modalElement && window.bootstrap) {
        const modal = new window.bootstrap.Modal(modalElement);
        modal.show();
      } else {
        console.error('Bootstrap modal is not available');
      }
    }, 3000);

    return () => clearTimeout(timer); // Cleanup timer on unmount
  }, []);

  // Handle modal close
  const handleClose = () => {
    setIsModalVisible(false);
  };

  return (
    <div className="modal fade" id="welcomeModal" tabIndex="-1" aria-labelledby="welcomeModalLabel" aria-hidden="true">
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header p-0">
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" onClick={handleClose}></button>
          </div>
          <div className="modal-body">
            <h4 className="modal-title text-center" id="welcomeModalLabel">Connecting you to the best live music, seven days a week</h4>
          </div>
          <div className="modal-footer">
            <button type="button" className="btn instant-btn" data-bs-dismiss="modal" onClick={handleClose}>Close</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WelcomeModal;
