import { useState } from 'react';
import { useAuth } from '../../context/AuthContext';

const Login = () => {
  const [input, setInput] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const { login } = useAuth(); // Access login from AuthContext

  const handleSubmitEvent = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: input.email,
          password: input.password,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        login({ token: data.token, role: data.role }); // Use login to set token and user info
        window.location.href = '/dashboard'; // Redirect after successful login
      } else {
        const errorData = await response.json();
        setError(errorData.message);
      }
    } catch (error) {
      setError('An error occurred. Please try again.');
    }
  };

  const handleInput = (e) => {
    const { name, value } = e.target;
    setInput({ ...input, [name]: value });
  };

  return (
    <div className="bg-custom">
      <div className="container ">
        <div className="row" style={{ justifyContent: 'center' }}>
          <div className="col-md-6 bg-black p-5">
            <form onSubmit={handleSubmitEvent} className="form mb-3">
              <div className="form_control">
                <label htmlFor="user-email">Email</label>
                <input
                  type="email"
                  id="user-email"
                  name="email"
                  aria-describedby="user-email"
                  aria-invalid="false"
                  onChange={handleInput}
                  className="form-control"
                />
              </div>
              <div className="form_control my-3">
                <label htmlFor="password">Password</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  aria-describedby="user-password"
                  aria-invalid="false"
                  onChange={handleInput}
                  className="form-control"
                />
              </div>
              <button className="btn instant-btn px-5">Login</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
