import React, { useEffect, useState, Suspense } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import emailjs from "emailjs-com";
import { Gallery, Item } from "react-photoswipe-gallery";
import "photoswipe/dist/photoswipe.css";
import ReactPlayer from "react-player";
import { FaWhatsapp } from "react-icons/fa"; 
import "./frontend.css";

const BsArrowLeftSquareFill = React.lazy(() =>
  import("react-icons/bs").then((module) => ({ default: module.BsArrowLeftSquareFill }))
);

const ArtistDetail = ({ onNavigate }) => {
  const { id } = useParams();
  const [artist, setArtist] = useState(null);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({ name: "", phone: "", email: "" });
  const [formMessage, setFormMessage] = useState("");
  const [images, setImages] = useState([]);

  const navigate = useNavigate();

  const handleBack = () => {
    if (onNavigate) {
      onNavigate(`/`);
    }
  };

  const getEmbedUrl = (url) => {
    const regex = /(?:youtube\.com\/(?:watch\?v=|embed\/|v\/)|youtu\.be\/)([a-zA-Z0-9_-]{11})/;
    const match = url?.match(regex);
    const videoId = match ? match[1] : null;
    return videoId ? `https://www.youtube.com/embed/${videoId}?rel=0` : null;
  };

  const addTargetToLinks = (html) =>
    html.replace(/<a /g, '<a target="_blank" rel="noopener noreferrer" ');

  useEffect(() => {
    const fetchArtist = async () => {
      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/artists/${id}`);
        setArtist(response.data);

        const imagePromises = response.data.galleryImages.map((img) =>
          new Promise((resolve) => {
            const src = `${process.env.REACT_APP_API_URL}/${img}`;
            const image = new Image();
            image.src = src;
            image.onload = () => resolve({ src, width: image.width, height: image.height });
          })
        );

        const loadedImages = await Promise.all(imagePromises);
        setImages(loadedImages);
      } catch (error) {
        console.error("Error fetching artist:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchArtist();
  }, [id]);

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    const templateParams = {
      name: formData.name,
      phone: formData.phone,
      email: formData.email,
      artistName: artist?.title,
    };

    emailjs
      .send(
        "service_3hkljmf",
        "template_tg0y1rn",
        templateParams,
        "q1l_DC7jwQvu80xJ5"
      )
      .then(() => {
        setFormMessage("Booking request sent successfully!");
        setFormData({ name: "", phone: "", email: "" });
      })
      .catch(() => {
        setFormMessage("Failed to send booking request. Please try again.");
      });
  };

  const whatsappShareUrl = `https://api.whatsapp.com/send?text=Check out this: ${window.location.href}`;

  return (
    <div className="artist-detail bg-custom">
      <div className="container">
        {/* Static Assets: Back Button */}
        <Suspense fallback={<div>Loading Back Button...</div>}>
          <span onClick={handleBack} className="back-btn">
            <BsArrowLeftSquareFill size={30} className="my-2" />
          </span>
        </Suspense>

        {/* Dynamic Content */}
        {loading ? (
          <div className="spinner">Loading artist details...</div>
        ) : (
          <>
            {/* Video Section */}
            {artist.videoUrl && (
              <div className="artist-video">
                <iframe
                  width="100%"
                  height="500"
                  src={getEmbedUrl(artist.videoUrl)}
                  title={artist.title}
                  frameBorder="0"
                  allowFullScreen
                ></iframe>
              </div>
            )}

            {/* Artist Details */}
            <h1>{artist?.title}</h1>
            <div id="description" className="mt-3">
              <h4>About</h4>
              <div
                dangerouslySetInnerHTML={{
                  __html: addTargetToLinks(artist.description || "<em>No description available</em>"),
                }}
              />
            </div>

            {/* Gallery */}
            {images.length > 0 && (
              <div className="gallery-container">
                <h4>Gallery</h4>
                <Gallery>
                  <div className="grid-container">
                    {images.map((img, index) => (
                      <Item
                        key={index}
                        original={img.src}
                        thumbnail={img.src}
                        width={img.width}
                        height={img.height}
                      >
                        {({ ref, open }) => (
                          <img
                            ref={ref}
                            onClick={open}
                            src={img.src}
                            alt={`Gallery image ${index + 1}`}
                            className="grid-item"
                            style={{ cursor: "pointer" }}
                          />
                        )}
                      </Item>
                    ))}
                  </div>
                </Gallery>
              </div>
            )}

            {/* Booking Form */}
            <div className="artistForm mt-3">
              <h1 className="mx-2 my-2">Book Now</h1>
              <form onSubmit={handleSubmit}>
                <div className="row">
                  <div className="col-md-6">
                    <input
                      type="text"
                      name="name"
                      className="form-control"
                      value={formData.name}
                      onChange={handleChange}
                      required
                      placeholder="Your Name"
                    />
                  </div>
                  <div className="col-md-6">
                    <input
                      type="tel"
                      name="phone"
                      className="form-control"
                      value={formData.phone}
                      onChange={handleChange}
                      required
                      placeholder="Phone Number"
                    />
                  </div>
                  <div className="col-md-6">
                    <input
                      type="email"
                      name="email"
                      className="form-control"
                      value={formData.email}
                      onChange={handleChange}
                      required
                      placeholder="Your Email"
                    />
                  </div>
                  <div className="col-md-6">
                    <button type="submit" className="btn enquirybtn">Book Now</button>
                  </div>
                </div>
              </form>
              {formMessage && <p className="form-message">{formMessage}</p>}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default ArtistDetail;
