import React, { useRef, useState, useEffect } from 'react';
import { gsap } from "gsap";

const Banner = () => {
  const slides = [
    {
      src: '/banner1.webp',
      alt: 'Banner for Musicians',
      text: 'Explore the World of Musicians',
    },
    {
      src: '/banner2.webp',
      alt: 'Banner for Venues',
      text: 'Find the Perfect Venue',
    },
    {
      src: '/banner3.webp',
      alt: 'Banner for Music Stores',
      text: 'Shop at Top Music Stores',
    },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const carouselRef = useRef(null);

  // Auto-play functionality
  useEffect(() => {
    const interval = setInterval(() => {
      goToNextSlide();
    }, 3000); // Change slide every 3 seconds
    return () => clearInterval(interval); // Cleanup on unmount
  }, []);

  const goToNextSlide = () => {
    const nextIndex = (currentIndex + 1) % slides.length;
    setCurrentIndex(nextIndex);
    animateSlide(nextIndex);
  };

  const goToSlide = (index) => {
    setCurrentIndex(index);
    animateSlide(index);
  };

  const animateSlide = (index) => {
    const xOffset = `-${index * 100}%`;
    gsap.to(carouselRef.current, {
      x: xOffset,
      duration: 1, // Smooth transition
      ease: 'power2.inOut',
    });
  };

  return (
    <div className="carousel-container" style={styles.carouselContainer}>
      <div
        className="carousel-track"
        style={styles.carouselTrack}
        ref={carouselRef}
      >
        {slides.map((slide, index) => (
          <div key={index} className="carousel-slide" style={styles.slide}>
            <img src={slide.src} alt={slide.alt} style={styles.image} />
            <div className="carousel-text" style={styles.text}>
              {slide.text}
            </div>
          </div>
        ))}
      </div>
      <div className="carousel-dots" style={styles.dots}>
        {slides.map((_, index) => (
          <span
            key={index}
            onClick={() => goToSlide(index)}
            style={{
              ...styles.dot,
              backgroundColor: currentIndex === index ? '#000' : '#ccc',
            }}
          ></span>
        ))}
      </div>
    </div>
  );
};

const styles = {
  carouselContainer: {
    position: 'relative',
    width: '100%',
    maxWidth: '100%',
    margin: 'auto',
    overflow: 'hidden',
  },
  carouselTrack: {
    display: 'flex',
    transition: 'transform 1s ease',
    willChange: 'transform',
  },
  slide: {
    flex: '0 0 100%',
    textAlign: 'center',
    position: 'relative',
  },
  image: {
    width: '100%',
    height: 'auto',
  },
  text: {
    position: 'absolute',
    bottom: '20px',
    left: '50%',
    transform: 'translateX(-50%)',
    backgroundColor: 'rgba(0, 0, 0, 0.6)',
    color: 'white',
    padding: '10px 20px',
    borderRadius: '5px',
  },
  dots: {
    display: 'flex',
    justifyContent: 'center',
    marginTop: '10px',
  },
  dot: {
    width: '12px',
    height: '12px',
    borderRadius: '50%',
    margin: '0 5px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
};

export default Banner;
