import React, { useState, useEffect } from "react";


const WelcomeModal = () => {
  const [isModalVisible, setIsModalVisible] = useState(false); // State to control modal visibility

  useEffect(() => {
    // Show the modal after 3 seconds
    const timer = setTimeout(() => {
      setIsModalVisible(true);
      const modal = new window.bootstrap.Modal(document.getElementById('welcomeModal'));
      modal.show();
    }, 3000);

    return () => clearTimeout(timer); // Cleanup timer on unmount
  }, []);

  // Handle modal close
  const handleClose = () => {
    setIsModalVisible(false);
  };

  return (
    <div
      className="modal fade"
      id="welcomeModal"
      tabIndex="-1"
      aria-labelledby="welcomeModalLabel"
      aria-hidden="true"
      inert={isModalVisible ? undefined : "true"} // Only use 'inert' when the modal is hidden
    >
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header p-0">
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" onClick={handleClose}></button>
          </div>
          <div className="modal-body">
            <h4 className="modal-title text-center" id="welcomeModalLabel">Connecting you to the best live music, seven days a week</h4>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WelcomeModal;
